import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FiHome, FiUser, FiUsers, FiSettings, FiLogOut, FiBarChart2, FiMenu } from "react-icons/fi";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          navigate("/signin");
          return;
        }

        const res = await fetch("http://localhost:5001/dashboard", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`, // Ensure Bearer token format
          },
        });

        if (res.status === 403) throw new Error("Unauthorized");

        const data = await res.json();
        setUser(data);
      } catch (error) {
        console.error("Dashboard error:", error);
        navigate("/signin");
      }
    };

    fetchUser();
  }, [navigate]);

  return (
    <div className="flex min-h-screen" >


      {/* Sidebar */}
      <div className="w-20 md:w-64 bg-blue-500 transition-width duration-300">
        <div className="flex justify-between items-center">
          <h2 className="text-2xl font-semibold hidden md:block">Admin Portal</h2>
          <button>
            <FiMenu size={24}/>
          </button>
        </div>
        <nav className="mt-4">
          <ul>
            <li className="flex items-center p-4 hover:bg-gray-700 cursor-pointer">
              <FiHome size={24} />
              <span className="ml-4 md:block">
                Home
              </span>
            </li>
            <li className="flex items-center p-4 hover:bg-gray-700 cursor-pointer">
              <FiUsers size={24} />
              <span className="ml-4 md:block">
                Users
              </span>
            </li>
            <li className="flex items-center p-4 hover:bg-gray-700 cursor-pointer">
              <FiSettings size={24} />
              <span className="ml-4 md:block">
                Settings
              </span>
            </li>
            <li className="flex items-center p-4 hover:bg-gray-700 cursor-pointer">
              <FiLogOut size={24} />
              <span className="ml-4 md:block">
                Logout
              </span>
            </li>
          </ul>
        </nav>
      </div>


      {/* Dashboard Content */}
      <div className="p-4 bg-gray-100 flex">
          <h1 className="text-2xl font-semibold">Dashboard</h1>
      </div>
    </div>
  );
};

export default Dashboard;
