import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          navigate("/signin");
          return;
        }

        const res = await fetch("http://backend:5001/dashboard", {
          method: "GET",
          headers: { Authorization: token },
        });

        if (!res.ok) throw new Error("Unauthorized");

        const data = await res.json();
        setUser(data.user);
      } catch (error) {
        console.error("Dashboard error:", error);
        navigate("/signin");
      }
    };

    fetchUser();
  }, [navigate]);

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white flex flex-col p-4">
        <h2 className="text-xl font-semibold mb-4">Welcome, {user?.username} üëã</h2>
        <nav>
          <ul className="space-y-2">
            <li>
              <a href="/dashboard" className="block px-3 py-2 rounded hover:bg-gray-700">üè† Home</a>
            </li>
            <li>
              <a href="/dashboard/profile" className="block px-3 py-2 rounded hover:bg-gray-700">üë§ Profile</a>
            </li>
            <li>
              <a href="/dashboard/users" className="block px-3 py-2 rounded hover:bg-gray-700">üë• Users</a>
            </li>
            <li>
              <a href="/dashboard/settings" className="block px-3 py-2 rounded hover:bg-gray-700">‚öôÔ∏è Settings</a>
            </li>
            <li>
              <button
                onClick={() => {
                  localStorage.removeItem("token");
                  navigate("/signin");
                }}
                className="w-full text-left px-3 py-2 mt-4 rounded bg-red-600 hover:bg-red-700"
              >
                üö™ Logout
              </button>
            </li>
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-6 bg-gray-100">
        <h1 className="text-3xl font-bold">Dashboard</h1>
        <p className="mt-2 text-gray-700">You are logged in as <span className="font-semibold">{user?.email}</span>.</p>
      </main>
    </div>
  );
};

export default Dashboard;
