import { useEffect, useState } from "react";
import { useNavigate, Link, useLocation } from "react-router-dom";
import { 
  Home, 
  User, 
  Users, 
  Settings, 
  LogOut, 
  BarChart2, 
  Menu, 
  Bell, 
  Search, 
  ChevronRight, 
  Calendar,
  CreditCard,
  HelpCircle,
  Plus
} from "lucide-react";
import { toast } from "sonner";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [notifications, setNotifications] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          navigate("/signin");
          return;
        }

        const res = await fetch("http://localhost:5001/dashboard", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });

        if (res.status === 403) throw new Error("Unauthorized");

        const data = await res.json();
        setUser(data);

        // Simulate notifications (remove in production)
        setNotifications([
          { id: 1, message: "New user registered", time: "Just now" },
          { id: 2, message: "Sales report available", time: "2 hours ago" },
          { id: 3, message: "System update completed", time: "Yesterday" },
        ]);

        toast.success("Welcome back to the dashboard");
      } catch (error) {
        console.error("Dashboard error:", error);
        toast.error("Authentication failed");
        navigate("/signin");
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    toast.success("Logged out successfully");
    navigate("/signin");
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-neutral-50">
        <div className="animate-pulse flex flex-col items-center">
          <div className="w-24 h-24 rounded-full bg-neutral-200"></div>
          <div className="mt-4 h-4 w-32 bg-neutral-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-neutral-50 overflow-hidden">
      {/* Sidebar */}
      <aside 
        className={`${
          sidebarOpen ? "w-64" : "w-20"
        } bg-white text-neutral-800 flex flex-col h-screen border-r border-neutral-200 transition-all duration-300 ease-in-out fixed md:relative z-10 shadow-sm`}
      >
        <div className={`p-6 flex items-center ${sidebarOpen ? "justify-between" : "justify-center"}`}>
          {sidebarOpen ? (
            <div className="text-xl font-medium">Workspace</div>
          ) : (
            <div className="w-8 h-8 rounded-md bg-neutral-100 flex items-center justify-center">
              <span className="text-lg font-medium">W</span>
            </div>
          )}
          <button 
            onClick={() => setSidebarOpen(!sidebarOpen)} 
            className="hover:bg-neutral-100 p-2 rounded-md transition-colors"
          >
            <Menu size={20} />
          </button>
        </div>

        <div className="flex-1 overflow-y-auto scrollbar-thin">
          <div className="px-4 py-2">
            {sidebarOpen && <p className="text-xs font-medium text-neutral-500 mb-2 ml-2">MENU</p>}
            <nav className="space-y-1">
              <NavItem 
                icon={<Home size={20} />} 
                text="Dashboard" 
                href="/dashboard" 
                active={location.pathname === "/dashboard"}
                collapsed={!sidebarOpen}
              />
              <NavItem 
                icon={<Users size={20} />} 
                text="Team" 
                href="/dashboard/team" 
                active={location.pathname.includes("/team")}
                collapsed={!sidebarOpen}
              />
              <NavItem 
                icon={<BarChart2 size={20} />} 
                text="Analytics" 
                href="/dashboard/analytics" 
                active={location.pathname.includes("/analytics")}
                collapsed={!sidebarOpen}
              />
              <NavItem 
                icon={<Calendar size={20} />} 
                text="Calendar" 
                href="/dashboard/calendar" 
                active={location.pathname.includes("/calendar")}
                collapsed={!sidebarOpen}
              />
              <NavItem 
                icon={<CreditCard size={20} />} 
                text="Billing" 
                href="/dashboard/billing" 
                active={location.pathname.includes("/billing")}
                collapsed={!sidebarOpen}
              />
            </nav>
          </div>

          {sidebarOpen && (
            <div className="px-4 py-2 mt-6">
              <p className="text-xs font-medium text-neutral-500 mb-2 ml-2">SETTINGS</p>
              <nav className="space-y-1">
                <NavItem 
                  icon={<User size={20} />} 
                  text="Profile" 
                  href="/dashboard/profile" 
                  active={location.pathname.includes("/profile")}
                  collapsed={!sidebarOpen}
                />
                <NavItem 
                  icon={<Settings size={20} />} 
                  text="Settings" 
                  href="/dashboard/settings" 
                  active={location.pathname.includes("/settings")}
                  collapsed={!sidebarOpen}
                />
                <NavItem 
                  icon={<HelpCircle size={20} />} 
                  text="Help" 
                  href="/dashboard/help" 
                  active={location.pathname.includes("/help")}
                  collapsed={!sidebarOpen}
                />
              </nav>
            </div>
          )}
        </div>

        <div className="p-4 border-t border-neutral-200">
          <button
            onClick={handleLogout}
            className={`w-full flex items-center gap-3 px-3 py-2 rounded-md hover:bg-red-50 hover:text-red-600 transition-colors ${!sidebarOpen && "justify-center"}`}
          >
            <LogOut size={18} />
            {sidebarOpen && <span>Logout</span>}
          </button>
        </div>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Top Navbar */}
        <header className="bg-white border-b border-neutral-200 py-4 px-6 flex justify-between items-center">
          <div className="flex items-center gap-2 md:gap-4">
            <button 
              onClick={() => setSidebarOpen(!sidebarOpen)} 
              className="md:hidden hover:bg-neutral-100 p-2 rounded-md transition-colors"
            >
              <Menu size={20} />
            </button>
            <h1 className="text-lg md:text-xl font-medium text-neutral-800">Dashboard</h1>
          </div>

          <div className="flex items-center gap-1 md:gap-4">
            <div className="relative hidden md:block">
              <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
                <Search size={16} className="text-neutral-400" />
              </div>
              <input 
                type="search" 
                placeholder="Search..." 
                className="py-2 pl-10 pr-4 bg-neutral-100 border-none rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-neutral-800 focus:bg-white w-64 transition-all"
              />
            </div>

            <div className="relative">
              <button className="p-2 hover:bg-neutral-100 rounded-md transition-colors relative">
                <Bell size={20} />
                <span className="absolute top-1 right-1 w-2 h-2 bg-red-500 rounded-full"></span>
              </button>

              {/* Notifications dropdown would go here in a production app */}
            </div>

            <div className="flex items-center gap-3 ml-2">
              <div className="hidden md:block text-right">
                <div className="text-sm font-medium">{user?.username || "Admin"}</div>
                <div className="text-xs text-neutral-500">Administrator</div>
              </div>
              <button className="w-9 h-9 rounded-full bg-neutral-200 flex items-center justify-center overflow-hidden hover:ring-2 hover:ring-neutral-800 transition-all">
                {user?.avatar ? (
                  <img src={user.avatar} alt="User" className="w-full h-full object-cover" />
                ) : (
                  <span className="font-medium text-sm">{user?.username?.charAt(0).toUpperCase() || "A"}</span>
                )}
              </button>
            </div>
          </div>
        </header>

        {/* Dashboard Content */}
        <main className="flex-1 overflow-auto p-6">
          <div className="max-w-7xl mx-auto">
            <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-8">
              <div>
                <h2 className="text-2xl font-semibold text-neutral-800">Welcome back</h2>
                <p className="text-neutral-500 mt-1">Here's an overview of your dashboard</p>
              </div>
              <button className="mt-4 md:mt-0 inline-flex items-center gap-2 bg-neutral-900 text-white px-4 py-2 rounded-md hover:bg-neutral-800 transition-colors">
                <Plus size={16} />
                <span>New Project</span>
              </button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
              <StatCard 
                title="Total Users" 
                value="1,284" 
                trend="+12.5%" 
                trendUp={true}
                icon={<Users className="text-blue-500" />} 
                color="blue"
              />
              <StatCard 
                title="Revenue" 
                value="$52,680" 
                trend="+8.2%" 
                trendUp={true}
                icon={<BarChart2 className="text-green-500" />} 
                color="green"
              />
              <StatCard 
                title="Active Sessions" 
                value="342" 
                trend="+24.5%" 
                trendUp={true}
                icon={<User className="text-amber-500" />} 
                color="amber"
              />
              <StatCard 
                title="Bounce Rate" 
                value="24.8%" 
                trend="-2.5%" 
                trendUp={false}
                icon={<ChevronRight className="text-red-500" />} 
                color="red"
              />
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              <div className="lg:col-span-2 bg-white rounded-xl shadow-sm border border-neutral-200 p-6 hover:shadow-md transition-shadow">
                <div className="flex items-center justify-between mb-6">
                  <h3 className="font-medium text-lg">Performance Overview</h3>
                  <select className="border border-neutral-200 rounded-md px-3 py-1.5 text-sm bg-white">
                    <option value="weekly">This Week</option>
                    <option value="monthly">This Month</option>
                    <option value="yearly">This Year</option>
                  </select>
                </div>
                <div className="h-64 flex items-center justify-center bg-neutral-50 rounded-lg">
                  <p className="text-neutral-400">Chart visualization would appear here</p>
                </div>
              </div>

              <div className="bg-white rounded-xl shadow-sm border border-neutral-200 p-6 hover:shadow-md transition-shadow">
                <h3 className="font-medium text-lg mb-6">Recent Activity</h3>
                <div className="space-y-4">
                  {notifications.map((notification) => (
                    <div key={notification.id} className="flex items-start gap-3 pb-4 border-b border-neutral-100">
                      <div className="w-2 h-2 rounded-full bg-blue-500 mt-2"></div>
                      <div>
                        <p className="text-sm">{notification.message}</p>
                        <p className="text-xs text-neutral-500 mt-1">{notification.time}</p>
                      </div>
                    </div>
                  ))}
                </div>
                <button className="mt-4 w-full text-center text-sm text-neutral-500 hover:text-neutral-800 py-2 border-t border-neutral-100 transition-colors">
                  View All Activity
                </button>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

// Sidebar Navigation Item Component
const NavItem = ({ icon, text, href, active, collapsed }) => (
  <Link
    to={href}
    className={`flex items-center ${
      collapsed ? "justify-center" : "justify-between"
    } px-3 py-2 rounded-md transition-colors ${
      active 
        ? "bg-neutral-900 text-white" 
        : "text-neutral-600 hover:bg-neutral-100"
    }`}
  >
    <div className="flex items-center gap-3">
      <div>{icon}</div>
      {!collapsed && <span className="font-medium text-sm">{text}</span>}
    </div>
    {!collapsed && active && <ChevronRight size={16} />}
  </Link>
);

// Statistic Card Component
const StatCard = ({ title, value, trend, trendUp, icon, color }) => {
  const getGradient = (color) => {
    const gradients = {
      blue: "from-blue-50 to-white",
      green: "from-green-50 to-white",
      amber: "from-amber-50 to-white",
      red: "from-red-50 to-white",
    };
    return gradients[color] || "from-neutral-50 to-white";
  };

  return (
    <div className={`bg-gradient-to-br ${getGradient(color)} rounded-xl p-6 border border-neutral-200 shadow-sm hover:shadow-md transition-all duration-300 group`}>
      <div className="flex justify-between items-start">
        <div className="p-2 rounded-lg bg-white shadow-sm border border-neutral-100">{icon}</div>
        <div className={`text-xs font-medium ${trendUp ? "text-green-600" : "text-red-600"} flex items-center gap-1`}>
          {trend}
          <span className={`w-0 h-0 border-l-4 border-r-4 border-l-transparent border-r-transparent ${trendUp ? "border-b-4 border-b-green-600" : "border-t-4 border-t-red-600"}`}></span>
        </div>
      </div>
      <div className="mt-4">
        <h3 className="text-sm font-medium text-neutral-500">{title}</h3>
        <p className="text-2xl font-semibold mt-1 group-hover:translate-x-1 transition-transform">{value}</p>
      </div>
    </div>
  );
};

export default Dashboard;
