import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FiHome, FiUser, FiUsers, FiSettings, FiLogOut, FiBarChart2 } from "react-icons/fi";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          navigate("/signin");
          return;
        }

        const res = await fetch("http://localhost:5001/dashboard", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`, // Ensure Bearer token format
          },
        });

        if (res.status === 403) throw new Error("Unauthorized");

        const data = await res.json();
        setUser(data);
      } catch (error) {
        console.error("Dashboard error:", error);
        navigate("/signin");
      }
    };

    fetchUser();
  }, [navigate]);

  return (
    <div className="flex">
      {/* Sidebar */}
      <div>
        <div className="flex justify-between items-center">
          <h2 className="text-2xl font-semibold">Admin Portal</h2>
        </div>
      </div>



      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Top Navbar */}
        <header className="bg-white shadow flex justify-between items-center px-6 py-4">
          <h1 className="text-2xl font-semibold">Dashboard</h1>
          <div className="text-gray-700 font-medium">{user?.username}</div>
        </header>

        {/* Dashboard Content */}
        <main className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">

          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
